    def cari_data(self):
        id_pem = self.lineEdit.text()

        if not id_pem:
            QtWidgets.QMessageBox.warning(
                None, "Error", "ID Pemeriksaan belum diisi"
            )
            return

        data = Pemeriksaan.select_detail_by_id(id_pem)

        if data:
            self.lineEdit.setText(str(data[0]))     # ID Pemeriksaan

            self.lineEdit_2.setText(str(data[1]))   # ID Dokter
            self.lineEdit_3.setText(data[2])        # Nama Dokter

            self.lineEdit_5.setText(str(data[3]))   # ID Perawat
            self.lineEdit_4.setText(data[4])        # Nama Perawat

            self.lineEdit_8.setText(str(data[5]))   # ID Pasien
            self.lineEdit_6.setText(data[6])        # Nama Pasien

            self.lineEdit_7.setText(str(data[7]))   # Tgl Pemeriksaan

            # üîí kunci ID Pemeriksaan
            self.lineEdit.setReadOnly(True)
            self.lineEdit.setStyleSheet("""
            QLineEdit {
                background-color: #f0f0f0;
                color: #555;
            }
            """)

            # ‚úèÔ∏è aktifkan edit tanggal
            self.lineEdit_7.setReadOnly(False)
            self.lineEdit_7.setStyleSheet("")

        else:
            QtWidgets.QMessageBox.warning(
                None, "Gagal", "Data pemeriksaan tidak ditemukan"
            )

    def update_data(self):
        id_pem = self.lineEdit.text()
        tanggal = self.lineEdit_7.text()

        if not id_pem or not tanggal:
            QtWidgets.QMessageBox.warning(None, "Error", "Data belum lengkap")
            return

        pemeriksaan = Pemeriksaan(id=id_pem, tanggal=tanggal)
        pemeriksaan.update_tanggal()

        QtWidgets.QMessageBox.information(
            None, "Sukses", "Tanggal pemeriksaan berhasil diperbarui"
        )

